import { stopSubmit } from 'redux-form';
import { profileAPI } from '../api/api';
import avImgOne from '../';

const ADD_POST = 'ADD-POST';
const SET_USER_PROFILE = 'SET_USER_PROFILE';
const SET_STATUS = 'SET_STATUS';
const SAVE_PHOTO_SUCCESS = 'SAVE_PHOTO_SUCCESS';

let initialState = {
    postsDt: [
        {
            id: 1,
            image: 'https://s589vla.storage.yandex.net/rdisk/04d605c886e1a9296004be8201bbd2066efd8b8815074a3c9ec030e0ae723a8e/66059f1f/Vs1ek84KiGlKGA7qH1ZuzjjVlrT0g8RDpwxGxWDAXIFeqL-llbNnids83lkErcxobB8iVy6uwGKFSMF8OQG8Vw==?uid=66490590&filename=avatar_1.png&disposition=inline&hash=&limit=0&content_type=image%2Fpng&owner_uid=66490590&fsize=32439&hid=ca50f9e8f1234d80e74e732d05b58562&media_type=image&tknv=v2&etag=45ad1333cd2b73e646698a310c410ee2&rtoken=ErZJKzzQFaeh&force_default=yes&ycrid=na-7fdbc955912d30a1d9f7a352b7089cbf-downloader20e&ts=614bb473ec5c0&s=2ad1aea41c002e4df655e3501397d654ae19afc2d2bd83a3ee0590227968209b&pb=U2FsdGVkX1-D0LchWUt55m2daenC47RK0_2Yi-7Am51zIKeupRNHWwYCyq5qEvH1OrUGDMSqnFBNBmkVJlddA0tB9Kpo2JVIUpsb_8VNnWo',
            message: 'Hi, how are u?',
            likes: 12
        },
        {
            id: 2,
            image: 'https://2.downloader.disk.yandex.ru/preview/7da3d437476c4aad8dabba41e1e2565ce2900b69874de7c86eb3d69694f14c22/inf/Tix-gSVI8Bj-KRpMU57kAIyYbpHdP2D0Ir23TlX7TBeH9ARgoeVVEiofHSq-O2ZYw8GSXR2bqutpbMkbXA_e_A%3D%3D?uid=66490590&filename=avatar_3.jpg&disposition=inline&hash=&limit=0&content_type=image%2Fjpeg&owner_uid=66490590&tknv=v2&size=3787x1960',
            message: 'Hi, everybody!',
            likes: 36
        },
        {
            id: 3,
            image: 'https://3.downloader.disk.yandex.ru/preview/83ad66e9f71cd64c4a7d102692459bbe5f598c9450793d72805c9c631b25c698/inf/d7SRNp0akLLOs9Rw2u4wkAUq3mOXuXt-6aPuVmYzo5fKvBU2IcDkmlwFOsuJF-6OFneT9bLlibFDuWuBpXx83A%3D%3D?uid=66490590&filename=avatar_5.jpg&disposition=inline&hash=&limit=0&content_type=image%2Fjpeg&owner_uid=66490590&tknv=v2&size=3787x1960',
            message: 'OK. And u?',
            likes: 26
        }
    ],
    userProfile: null,
    status: ''
};

const profileReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_POST:
            /*return {
                ...state,
                postsDt: [...state.postsDt, {
                    id: state.postsDt.length + 1,
                    message: action.newPost,
                    likes: 0
                }]
            };*/
            let newPost = {
                id: state.postsDt.length + 1,
                message: action.newPostText,
                likes: 0
            }
            return {
                ...state,
                postsDt: [...state.postsDt, newPost],
                newPostText: ''
            };
        case SET_USER_PROFILE:
            return {
                ...state,
                userProfile: action.userProfile
            };
        case SET_STATUS:
            return {
                ...state,
                status: action.status
            };
        case SAVE_PHOTO_SUCCESS:
            return {
                ...state,
                profile: {
                    ...state.profile, 
                    photos: action.photos
                }
            }
        default:
            return state;
    };
};

//dispatch
//export const addPostActionCreator = (newPost) => ({type: ADD_POST, newPost});
export const addPostActionCreator = (text) => ({type: ADD_POST, newPostText: text});
export const setUserProfile = (userProfile) => ({type: SET_USER_PROFILE, userProfile});
export const setStatus = (status) => ({type: SET_STATUS, status});
export const savePhotoSuccess = (photos) => ({type: SAVE_PHOTO_SUCCESS, photos});

//thunk
export const getUserProfile = (userId) => async (dispatch) => {
    let response = await profileAPI.getProfile(userId);
    dispatch(setUserProfile(response.data));
}

export const getUserStatus = (userId) => async (dispatch) => {
    let response = await profileAPI.getStatus(userId);
    dispatch(setStatus(response.data));
}

export const updateUserStatus = (status) => async (dispatch) => {
    let response = await profileAPI.updateStatus(status);
    if (response.data.resultCode === 0) {
        dispatch(setStatus(status));
    }
}

export const savePhoto = (file) => async (dispatch) => {
    let response = await profileAPI.savePhoto(file);
    if (response.data.resultCode === 0) {
        dispatch(savePhotoSuccess(response.data.data.photos));
    }
}

export const saveProfile = (profile) => async (dispatch, getState) => {
    const userId = getState().auth.userId;
    const response = await profileAPI.saveProfile(profile);
    if (response.data.resultCode === 0) {
        dispatch(getUserProfile(userId));
    } else {
        dispatch(stopSubmit('editProfile', {
            _error: response.data.messages[0]
        }));
        return Promise.reject(response.data.messages[0]);
    }
}

export default profileReducer;
