export const requiredField = value => {
    if (value) return undefined;

    return 'Обязательное к заполнению поле';
}

export const  maxLengthCreator = value => {
    if (value && value.length > 30) return 'Максимальное количество знаков должно равняться 30';

    return undefined;
}
