import React from 'react';
import classes from '../../css/Users/Users.module.css';
import userPhoto from '../../assets/img/no-pic.png';
import { NavLink } from 'react-router-dom';

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i=1; i <= pagesCount; i++) {
        pages.push(i);
    }
    
    return <div>
        <div className={classes.nav_pages}>
            {pages.map(p => {
                return (
                    <span 
                    key={p.id}
                    className={props.currentPage === p && classes.selected_page || classes.page}
                    onClick={(e) => {props.onPageChanged(p)}}>
                        {p}
                    </span>
                );
            })}
        </div>
        {
            props.users.map(u => <div key={u.id}>
                <div>
                    <NavLink to={'/profile/' + u.id}>
                        <img 
                            src={u.photos.small !== null ? u.photos.small : userPhoto} 
                            alt='' 
                            className={classes.user_photo} />
                    </NavLink>
                    <div>
                        {u.followed 
                        ? <button onClick={ () => { 
                            props.unfollow(u.id) 
                        } }>Отписаться</button> 
                        : <button onClick={ () => { 
                            
                            axios
                                .post(`https://social-network.samuraijs.com/api/1.0/follow?page=${u.id}, `)
                                .then(response => {
                                    this.props.setIsFetching(false);
                                    this.props.setUsers(response.data.items);
                                    this.props.setTotalUsersCount(response.data.totalCount);
                                });
                            
                            props.follow(u.id) 
                        } }>Подписаться</button>}
                    </div>
                </div>
                <div>
                    <div>
                        <span>{u.name}</span>
                        <span>{u.status}</span>
                    </div>
                    <div>
                        <span>{'u.location.city'}</span>
                        <span>{'u.location.country'}</span>
                    </div>
                </div>
            </div>)
        }    
    </div>
}

export default Users;
