import React from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileStatus.module.css';

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    };

    onEditMode = () => {
        this.setState( {
            editMode: true
        });
    }

    offEditMode = () => {
        this.setState( {
            editMode: false
        });
        this.props.updateStatus(this.statusInputRef.current.value);
    }

    render() {
        return (
            <div>
                { !this.state.editMode && 
                    <div className={ classes.profile_status }>
                        <span onDoubleClick={ this.onEditMode }>
                            { this.props.status }
                        </span>
                    </div>
                }
                { this.state.editMode &&
                    <div className={ classes.profile_status__input }>
                        <input 
                            autoFocus={ true } 
                            onBlur={ this.offEditMode } 
                            defaultValue={ this.props.status }
                            ref={ this.statusInputRef } />
                    </div>
                }
            </div>
        );
    }
};

export default ProfileStatus;
