import React from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileStatus.module.css';

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    };

    onEditMode() {
        this.setState( {
            editMode: true
        });
    }

    offEditMode() {
        this.setState( {
            editMode: false
        });
    }

    render() {
        return (
            <div>
                { !this.state.editMode && 
                    <div className={ classes.profile_status }>
                        <span onDoubleClick={ this.onEditMode.bind(this) }>
                            { this.props.status }
                            </span>
                    </div>
                }
                { this.state.editMode &&
                    <div className={ classes.profile_status__input }>
                        <input autoFocus={ true } onBlur={ this.offEditMode.bind(this) } defaultValue={ this.props.status } />
                    </div>
                }
            </div>
        );
    }
};

export default ProfileStatus;
