import React from 'react';
import classes from '../../css/FormControls.module.css';

const FormControl = ({meta, input, element, ...props}) => {
    const hasError = meta.touched && meta.error;

    return (
        <label className={ hasError ? classes.error : '' }>
            { React.createElement(element, {...input, ...props, className: hasError && classes.error}, null) }
            { hasError && <span>{meta.error}</span>}
        </label>
    )
}

export const Textarea = (props) => {
    return <FormControl {...props} element={'textarea'}></FormControl>
}

export const Input = (props) => {
    return <FormControl {...props} element={'input'}></FormControl>
}

export const CreateField = (component, name, placeholder, validators) => <Field 
component={component}
name={name}
placeholder={placeholder}
validate={[requiredField]} />
