import React from 'react';
import { Field, reduxForm } from 'redux-form';
import classes from '../../css/Auth/Auth.module.css';
import { Input } from '../../utils/FormControls/FormControls';
import { requiredField } from '../../utils/Validators/Validators';
import { connect } from 'react-redux';
import { Authorization } from '../../redux/authReducer';

const SignInForm = (props) => {
    return ( 
        <form 
            className={ classes.signin_form }
            onSubmit={ props.handleSubmit }>
            <div>
                <Field 
                    component={ Input }
                    name='email'
                    placeholder='E-mail'
                    validate={[requiredField]} />
            </div>
            <div>
                <Field
                    component={ Input }
                    name='password'
                    placeholder='Пароль'
                    type='password'
                    validate={[requiredField]} />
            </div>
            <div>
                <label className='input_checkbox__customized'>
                    <Field
                        component={ Input }
                        name='rememberMe'
                        type='checkbox' />
                    Запомнить меня
                </label>
            </div>
            <div>
                <button type='submit'>
                    Авторизоваться
                </button>
            </div>
        </form>
    )
}

const SignInReduxForm = reduxForm({form: 'signIn'})(SignInForm)

const Auth = (props) => {
    const onSubmit = (formData) => {
        props.Authorization(formData.email, formData.password, formData.rememberMe)
    }

    if (props.isAuthorized) {
        return
    }

    return <div>
        <h1 className={ classes.page_title }>
            Авторизация
        </h1>
        <SignInReduxForm onSubmit={onSubmit} />
    </div>
}

export default connect(null, { Authorization })(Auth);
