import React from 'react';
import { useFormik } from 'formik';
import classes from '../../css/Auth/Auth.module.css';

const Auth = (props) => {
    return <div>
        <h1 className={classes.page_title}>
            Авторизация
        </h1>
        <SignInForm />
    </div>
}

const SignInForm = (props) => {
    const formik = useFormik({
        initialValues: {
          email: '',
          
        },
        onSubmit: values => {
          alert(JSON.stringify(values, null, 2));
        },
      });
    return ( 
        <form 
            className={ classes.signin_form }
            onSubmit={ formik.handleSubmit }>
            <div>
                <input 
                    component={'input'}
                    name={'login'}
                    placeholder={'Имя'} />
            </div>
            <div>
                <input
                    component={'input'}
                    name={'password'}
                    placeholder={'Пароль'} />
            </div>
            <div>
                <label className='input_checkbox__customized'>
                    <input
                        component={'input'}
                        name={'rememberMe'}
                        type={'checkbox'} />
                    Запомнить меня
                </label>
            </div>
            <div>
                <button type={'submit'}>
                    Авторизоваться
                </button>
            </div>
        </form>
    )
}

//const SignInReduxForm = reduxForm({form: 'signIn'})(SignInForm)

export default Auth;
