import React from 'react';
import classes from '../../css/Auth/Auth.module.css';
import { Form, Field } from 'react-final-form';

const Auth = (props) => {
    return <div>
        <h1 className={classes.page_title}>
            Авторизация
        </h1>
        <SignInForm />
    </div>
}

const SignInForm = (props) => {
    return (
        <Form
            onSubmit={values => {}}
            validate={values => {}}
            render={({ handleSubmit }) => (
                <form 
                    className={classes.signin_form}
                    onSubmit={handleSubmit}>
                    <div>
                        <Field 
                            component={'input'}
                            name={'login'}
                            placeholder={'Имя'} />
                    </div>
                    <div>
                        <Field 
                            component={'input'}
                            name={'password'}
                            placeholder={'Пароль'} />
                    </div>
                    <div>
                        <Field 
                            component={'input'}
                            name={'rememberMe'}
                            type={'checkbox'} />
                        Запомнить меня
                    </div>
                    <div>
                        <button type={'submit'}>
                            Авторизоваться
                        </button>
                    </div>
                </form>
            )}>
        </Form>
    )
}

//const SignInReduxForm = reduxForm({form: 'signIn'})(SignInForm)

export default Auth;
