const ADD_POST = 'ADD-POST';
const REWRITE_POST_TEXT = 'REWRITE-POST-TEXT';
const ADD_MSG = 'ADD-MSG';
const NEW_MSG_POST = 'NEW-MSG-POST';

let store = {
    //свойства
    _state: {
        pageProfile: {
            postsDt: [
                {
                    id: 1,
                    message: 'Hi, how are u?',
                    likes: 12
                },
                {
                    id: 2,
                    message: 'Hi, everybody!',
                    likes: 36
                },
                {
                    id: 3,
                    message: 'OK. And u?',
                    likes: 26
                }
            ],
            newPostText: 'IT project'
        },
        pageDialogues: {
            dialoguesDt: [
                {
                    id: 1,
                    name: 'Мария',
                    image: 'avatar_1.png'
                },
                {
                    id: 2,
                    name: 'Антон',
                    image: 'avatar_2.jpg'
                },
                {
                    id: 3,
                    name: 'Мартин',
                    image: 'avatar_5.jpg'
                },
                {
                    id: 4,
                    name: 'Алексей',
                    image: 'avatar_4.jpg'
                },
                {
                    id: 5,
                    name: 'Евгения',
                    image: 'avatar_3.jpg'
                }
            ],
            messagesDt: [
                {
                    id: 1,
                    message: 'Hi!',
                    sender: 'friend'
                },
                {
                    id: 2,
                    message: 'How r u?',
                    sender: 'friend'
                },
                {
                    id: 3,
                    message: 'OK. And you?',
                    sender: 'im'
                },
                {
                    id: 4,
                    message: 'Awesome!',
                    sender: 'friend'
                }
            ],
            newMessageText: 'IT development'
        },
        sidebar: {
            friends: [
                {
                    id: 1,
                    name: 'Мария Кононова',
                    image: 'avatar_1.png' 
                },
                {
                    id: 2,
                    name: 'Антон Роднинский',
                    image: 'avatar_2.jpg'
                },
                {
                    id: 3,
                    name: 'Мартин Чан',
                    image: 'avatar_5.jpg'
                },
                {
                    id: 4,
                    name: 'Алексей Малик',
                    image: 'avatar_4.jpg'
                },
                {
                    id: 5,
                    name: 'Евгения Русланова',
                    image: 'avatar_3.jpg'
                }
            ]
        }
    },
    //методы
    _callSubscriber() {
        console.log('State is changed.');
    },
    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    },
    dispatch(action) {
        if (action.type === ADD_POST) {
            let newPost = {
                id: 4,
                message: this._state.pageProfile.newPostText,
                likes: 0
            };
            this._state.pageProfile.postsDt.push(newPost);
            this._state.pageProfile.newPostText = '';
            this._callSubscriber(this._state);
        } else if (action.type === REWRITE_POST_TEXT) {
            this._state.pageProfile.newPostText = action.newTxt;
            this._callSubscriber(this._state);
        } else if (action.type === ADD_MSG) {
            let newMsg = {
                id: 5,
                message: this._state.pageDialogues.newMessageText,
                sender: 'im'
            };
            this._state.pageDialogues.messagesDt.push(newMsg);
            this._state.pageDialogues.newMessageText = '';
            this._callSubscriber(this._state);
        } else if (action.type === NEW_MSG_POST) {
            this._state.pageDialogues.newMessageText = action.newMsg;
            this._callSubscriber(this._state);
        }
    }
}

export const addPostActionCreator = () => ({type: ADD_POST});

export const rewritePostTextActionCreator = (text) => ({type: REWRITE_POST_TEXT, newTxt: text});

export const addMsgActionCreator = () => ({type: ADD_MSG});

export const newMsgPostActionCreator = (text) => {
    return {
        type: NEW_MSG_POST, newMsg: text
    };
};

export default store;

window.store = store;
