import React from 'react';
import classes from '../../css/Users/Users.module.css';
import noPhoto from '../../assets/img/no-photo.png';
import { NavLink } from 'react-router-dom';

const User = ({user, followingInProgress, follow, unfollow}) => {
    let u = user;
    return (
        <div>
            <div className={classes.info}>
                <NavLink to={'/profile/' + u.id}>
                    <img 
                        src={u.photos.small !== null ? u.photos.small : noPhoto} 
                        alt='' 
                        className={classes.user_photo} />
                </NavLink>
                <div className={classes.user_info}>
                    <div>
                        <span className={classes.u_name}>{u.name}</span>
                        <span>{u.status}</span>
                    </div>
                    <div>
                        <span>{'u.location.city'}</span>
                        <span>{'u.location.country'}</span>
                    </div>
                </div>
            </div>
            <div className={classes.follow_buttons}>
                {u.followed 
                ? <button 
                    disabled={followingInProgress.some(id => id === u.id)} 
                    className={classes.unfollow_btn} 
                    onClick={() => {unfollow(u.id)}}>
                        Отписаться</button> 
                : <button 
                    disabled={followingInProgress.some(id => id === u.id)}
                    className={classes.follow_btn} 
                    onClick={ () => {follow(u.id)}}>
                    Подписаться</button>
                }
            </div>
        </div>
    )
}

export default User;
