import {rerenderEntireTree} from '../render';

let state = {
    pageProfile: {
        postsDt: [
            {
                id: 1,
                message: 'Hi, how are u?',
                likes: 12
            },
            {
                id: 2,
                message: 'Hi, everybody!',
                likes: 36
            },
            {
                id: 3,
                message: 'OK. And u?',
                likes: 26
            }
        ],
        newPostText: 'IT project'
    },
    pageDialogues: {
        dialoguesDt: [
            {
                id: 1,
                name: 'Мария',
                image: 'avatar_1.png'
            },
            {
                id: 2,
                name: 'Антон',
                image: 'avatar_2.jpg'
            },
            {
                id: 3,
                name: 'Мартин',
                image: 'avatar_5.jpg'
            },
            {
                id: 4,
                name: 'Алексей',
                image: 'avatar_4.jpg'
            },
            {
                id: 5,
                name: 'Евгения',
                image: 'avatar_3.jpg'
            }
        ],
        messagesDt: [
            {
                id: 1,
                message: 'Hi!',
                sender: 'friend'
            },
            {
                id: 2,
                message: 'How r u?',
                sender: 'friend'
            },
            {
                id: 3,
                message: 'OK. And you?',
                sender: 'im'
            },
            {
                id: 4,
                message: 'Awesome!',
                sender: 'friend'
            }
        ]
    },
    sidebar: {
        friends: [
            {
                id: 1,
                name: 'Мария Кононова',
                image: 'avatar_1.png' 
            },
            {
                id: 2,
                name: 'Антон Роднинский',
                image: 'avatar_2.jpg'
            },
            {
                id: 3,
                name: 'Мартин Чан',
                image: 'avatar_5.jpg'
            },
            {
                id: 4,
                name: 'Алексей Малик',
                image: 'avatar_4.jpg'
            },
            {
                id: 5,
                name: 'Евгения Русланова',
                image: 'avatar_3.jpg'
            }
        ]
    }
}

export let addPost = () => {
    let newPost = {
        id: 4,
        message: state.pageProfile.newPostText,
        likes: 0
    };
    state.pageProfile.postsDt.push(newPost);
    state.pageProfile.newPostText = '';
    rerenderEntireTree(state);
};

export let rewritePostText = (newTxt) => {
    let newPost = {
        id: 4,
        message: postMessage,
        likes: 0
    };
    state.pageProfile.newPostText = newTxt;
    rerenderEntireTree(state);
};

export let addMsg = () => {
    let newMsg = {
        id: 5,
        message: state.pageDialogues.newMsgTxt,
        sender: 'im'
    };
    state.pageDialogues.messagesDt.push(newPost);
    state.pageDialogues.newMsgTxt = '';
    rerenderEntireTree(state);
};

export let newMsgPost = (newMsgTxt) => {
    let newMsg = {
        id: 4,
        message: sendMsg,
        likes: 0
    };
    state.pageProfile.newPostText = newMsg;
    rerenderEntireTree(state);
};

export default state;
