import React from 'react';
import classes from '../../css/FormControls.module.css';

const FormControl = ({meta, input, element, ...props}) => {
    const hasError = meta.touched && meta.error;

    return (
        <label className={ hasError ? classes.error : '' }>
            <children.type {...input} {...children.props} />
            { hasError && <span>{meta.error}</span>}
        </label>
    )
}

export const Textarea = (props) => {
    return <FormControl {...props}><textarea /></FormControl>
}

export const Input = (props) => {
    return <FormControl {...props}><input /></FormControl>
}
