import React, { useState } from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileStatus.module.css';

const ProfileStatusWithHooks = (props) => {
    let [editMode, setEditMode] = useState({
        editMode: false,
        status: props.status
    });

    const onEditMode = () => {
        setEditMode(true);
    }

    const offEditMode = () => {
        setEditMode(false);
        //props.updateStatus(state.status);
    }

    return (
        <div>
            { !editMode &&
                <div className={ classes.profile_status }>
                    <span onDoubleClick={ onEditMode }>
                        { props.status || '–' }
                    </span>
                </div>
            }
            { editMode &&
                <div className={ classes.profile_status__input }>
                    <input 
                        autoFocus={ true }
                        onBlur={ offEditMode } />
                </div>
            }
        </div>
    );
};

export default ProfileStatusWithHooks;
