import React from 'react';
import {addPostActionCreator, rewritePostTextActionCreator} from '../../../redux/profileReducer'
import MyPosts from './MyPost';

/*const MyPostsContainer = (props) => {
    let state = props.store.getState();

    let addPost = () => {
        props.store.dispatch(addPostActionCreator());
    };

    let onPostChange = (text) => {
        let action = rewritePostTextActionCreator(text);
        props.store.dispatch(rewritePostTextActionCreator(text));
    };

    return (
        <MyPosts 
            updateNewPostText={onPostChange} 
            addPost={addPost} 
            posts={state.pageProfile.postsDt}
            newPostText={state.pageProfile.newPostText} />
    );
}*/

let mapStateToProps = (state) => {
    return {
        pageProfile: state.pageProfile
    }
};

let mapDispatchToProps = (dispatch) => {
    return {
        updateNewPostText: (text) => {
            dispatch(rewritePostTextActionCreator(text));
        },
        addPost: () => {
            dispatch(addPostActionCreator());
        }
    }
};

const MyPostsContainer = connect(mapStateToProps, mapDispatchToProps)(MyPosts);

export default MyPostsContainer;
