import React from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileInfo.module.css';
import Preloader from '../../Preloader/Preloader';

const ProfileInfo = (props) => {
    let ContactsElements = Object.entries(props.profile.contacts).map(([key,value],c) =>
        {
            if([value]!='') return(
                <div className={s.contacts__item}>
                    <a href={`${key}`} className={s.contacts__link}>
                        {`${value}`}
                    </a>
                </div>
        )}
    );

    if (!props.profile) {
        return <Preloader />
    }

    return (
        <div>
            <div className={classes.top_img}>
                <img src="https://avatars.dzeninfra.ru/get-zen_doc/196027/pub_5cb2e7b9c07b6700b34e992c_5cb2ebb66749e800b419fc58/scale_2400" alt=''/>
            </div>
            <div className={classes.info}>
                <div className={classes.avatar}>
                    <img src={props.profile.photos.large} alt='' />
                </div>
                <div className={classes.data}>
                    <div className={classes.fio}>
                        {props.profile.fullName}
                    </div>
                    <div>
                        <span><b>Обо мне:</b> {props.profile.aboutMe}</span>
                        <span><b>Ищу работу:</b> {props.profile.lookingForAJob ? 'Да' : 'Нет'}</span>
                        <span><b>Описание работы:</b> {props.profile.lookingForAJobDescription}</span>
                        <span>
                            <b>Контакты: </b>
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProfileInfo;
