import React from 'react';
import classes from '../../../css/Dialogues/Message/NewMessage.module.css';
import {addMsgCreator, newMsgPostCreator} from '../../../redux/state'

const NewMessage = (props) => {
    let nwMsg = React.createRef();

    let addMsg = () => {
        props.dispatch(addMsgActionCreator());
    };

    let onMsgChange = () => {
        let text = nwMsg.current.value,
        action = newMsgPostActionCreator(text);;
        props.dispatch(action);
    };

    return (
        <div>
            <form className={classes.form}>
                <textarea onChange={onMsgChange} ref={nwMsg} value={props.newMessageText} />
                <button type='button' onClick={addMsg}>
                    Отправить
                </button>
            </form>
        </div>
    );
}

export default NewMessage;
