import React from 'react';
import classes from '../../css/Users/Users.module.css';
import userPhoto from '../../assets/img/no-pic.png';
import { NavLink } from 'react-router-dom';
import { usersAPI } from '../../api/api';

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i=1; i <= pagesCount; i++) {
        pages.push(i);
    }
    
    return <div>
        <div className={classes.nav_pages}>
            {pages.map(p => {
                return (
                    <span 
                    key={p.id}
                    className={props.currentPage === p && classes.selected_page || classes.page}
                    onClick={(e) => {props.onPageChanged(p)}}>
                        {p}
                    </span>
                );
            })}
        </div>
        {
            props.users.map(u => 
                <div key={u.id} className={classes.users_list}>
                    <div>
                        <NavLink to={'/profile/' + u.id}>
                            <img 
                                src={u.photos.small !== null ? u.photos.small : userPhoto} 
                                alt='' 
                                className={classes.user_photo} />
                        </NavLink>
                    </div>
                    <div className={classes.follow_buttons}>
                        {u.followed 
                        ? <button className={classes.unfollow_btn} onClick={ () => { 
                            usersAPI.unfollowUser(u.id).then(data => {
                                if (data.resultCode == 0) {
                                    props.unfollow(u.id)
                                };
                            }); 
                        } }>Отписаться</button> 
                        : <button className={classes.follow_btn} onClick={ () => {
                            usersAPI.followUser(u.id).then(data => {
                                if (data.resultCode == 0) {
                                    props.follow(u.id);
                                };
                            });  
                        } }>Подписаться</button>}
                    </div>
                </div>
            )
        }    
    </div>
}

export default Users;
