import React from 'react';
import { Field, reduxForm } from 'redux-form';
import Post from '../MyPosts/Post';
import classes from '../../../css/Profile/MyPosts/MyPost.module.css';

const MyPosts = (props) => {
    let postsEls = props.posts.map(msg => <Post message={msg.message} likes={msg.likes} key={msg.id} />);

    let newPostEl = React.createRef();

    let onAddPost = () => {
        props.addPost();
    };

    let onPostChange = () => {
        let text = newPostEl.current.value;
        props.updateNewPostText(text);
    };

    return (
        <div className={classes.entries}>
            <p>My posts</p>
            
            {postsEls}
        </div>
    );
}

const AddPostForm = (props) => {
    return (
        <div>
            <form 
                className={classes.form}
                onSubmit={ props.handleSubmit }>
                <Field 
                    component='textarea'
                    name='newMessage'
                    placeholder='Введите сообщение' />
                <button type='submit'>
                    Отправить
                </button>
            </form>
        </div>
    )
}

const AddPostFormReduxForm = reduxForm({form: 'addPost'})(AddPostForm)

export default MyPosts;
