import React from 'react';
import classes from '../../css/Dialogues/Dialogues.module.css';
import DialogueItem from '../Dialogues/DialogueItem/DialogueItem';
import Message from '../Dialogues/Message/Message';
import NewMessage from '../Dialogues/Message/NewMessage';

const Dialogues = (props) => {

    let dialoguesEls = props.state.dialoguesDt
        .map(chat => <DialogueItem name={chat.name} id={chat.id} image={chat.image} />);

    let messagesEls = props.state.messagesDt.map(msg => <Message message={msg.message} sendFrom={msg.sender} />);

    return (
        <div className={classes.dialogues}>
            <div className={classes.dialogues_items}>
                {dialoguesEls}
            </div>
            <div className={classes.messages}>
                {messagesEls}    
                <NewMessage
                    addMsg={props.addMsg}
                    newPostText={props.pageProfile.newPostText}
                    rewritePostText={props.rewritePostText} />
            </div>
        </div>
    );
}

export default Dialogues;
