import React from 'react';
import Post from '../MyPosts/Post';
import classes from '../../../css/Profile/MyPosts/MyPost.module.css';

const MyPosts = (props) => {

    let postsData = [
        {id: 1, message: 'Hi, how are u?', likes: 11},
        {id: 2, message: 'Hi, everybody!', likes: 30},
        {id: 3, message: 'OK. And u?', likes: 26}
    ]

    let postsEls = props.postsData.map(post => <Post message={post.message} likes={post.likes} />);

    return (
        <div className={classes.entries}>
            <p>My posts</p>
            <div>
                <form>
                    <textarea></textarea>
                    <button>Отправить</button>
                </form>
            </div>
            {postsEls}
        </div>
    );
}

export default MyPosts;
