const ADD_POST = 'ADD-POST';
const REWRITE_POST_TEXT = 'REWRITE-POST-TEXT';

const profileReducer = (state, action) => {
    switch(action.type) {
        case ADD_POST:
            let newPost = {
                id: 4,
                message: state.newPostText,
                likes: 0
            };
            state.postsDt.push(newPost);
            state.newPostText = '';
        case REWRITE_POST_TEXT:
            state.newPostText = action.newTxt;
    };
    if (action.type === ADD_POST) {
        let newPost = {
            id: 4,
            message: state.newPostText,
            likes: 0
        };
        state.postsDt.push(newPost);
        state.newPostText = '';
    } else if (action.type === REWRITE_POST_TEXT) {
        state.newPostText = action.newTxt;
    }

    return state;
};

export default profileReducer;
