import React from 'react';
import {Link} from 'react-router-dom';
import classes from '../css/Nav.module.css';

const setActive = ({ isActive }) => isActive ? "active-link" : "";

/*const Nav = () => {
    return (
        <nav className={classes.nav}>
            <div>
                <Link className={setActive} to="/profile">
                    Профиль
                </Link>
            </div>
            <div>
                <Link className={setActive} to="/messages">
                    Сообщения
                </Link>
            </div>
            <div>
                <Link className={setActive} to="/news">
                    Новости
                </Link>
            </div>
            <div>
                <Link className={setActive} to="/music">
                    Музыка
                </Link>
            </div>
            <div>
                <Link className={setActive} to="/settings">
                    Настройки
                </Link>
            </div>
      </nav>
    );
}
*/

import { NavLink } from "react-router-dom";

<NavLink
  to="/messages"
  className={({ isActive, isPending }) =>
    isPending ? "pending" : isActive ? "active" : ""
  }
>
  Messages
</NavLink>;

export default Nav;
