import React from 'react';
import classes from '../../../css/Dialogues/Message/NewMessage.module.css';

const NewMessage = (props) => {
    let newTxtMsg = React.createRef();
    let addMsg = () => {
        let msgText = newTxtMsg.value;
        props.addMsg();
        alert(msgText);
    };

    let onMsgChange = () => {
        let msgText = newTxtMsg.value;
        props.newMsgPost(msgText);
    };

    return (
        <div>
            <form className={classes.form}>
                <textarea onChange={onMsgChange} ref={newTxtMsg} value={props.newMessageText} />
                <button type='button' onClick={addMsg}>
                    Отправить
                </button>
            </form>
        </div>
    );
}

export default NewMessage;
