import React from 'react';
import classes from '../../css/Users/Users.module.css';
import axios from 'axios';

const Users = (props) => {

    if (props.users.length === 0) {

        axios
            .get('https://social-network.samuraijs.com/api/1.0/users')
            .then();

        props.setUsers([
            {
                id: 1,
                photoURL: 'https://shapka-youtube.ru/wp-content/uploads/2021/02/avatarka-dlya-skaypa-dlya-parney.jpg',
                fullName: 'Святослав',
                status: 'Big Boss',
                followed: false,
                location: {
                    city: 'Новосибирск',
                    country: 'Россия'
                }
            },
            {
                id: 2,
                photoURL: 'https://cs14.pikabu.ru/post_img/big/2023/02/13/8/1676295806139337963.png',
                fullName: 'Марина',
                status: 'Big Boss',
                followed: true,
                location: {
                    city: 'Минск',
                    country: 'Беларусь'
                }
            },
            {
                id: 3,
                photoURL: 'https://masterpiecer-images.s3.yandex.net/fec2be5d968d11eeb6343a7ca4cc1bdc:upscaled',
                fullName: 'Aaron',
                status: 'Big Boss',
                followed: true,
                location: {
                    city: 'NY',
                    country: 'USA'
                }
            }
        ]);
    }

    return <div>
        {
            props.users.map(u => <div key={u.id}>
                <div>
                    <img src={u.photoURL} alt='' className={classes.user_photo} />
                    <div>
                        {u.followed 
                        ? <button onClick={ () => { props.unfollow(u.id) } }>Отписаться</button> 
                        : <button onClick={ () => { props.follow(u.id) } }>Подписаться</button>}
                    </div>
                </div>
                <div>
                    <div>
                        <span>{u.fullName}</span>
                        <span>{u.status}</span>
                    </div>
                    <div>
                        <span>{u.location.city}</span>
                        <span>{u.location.country}</span>
                    </div>
                </div>
            </div>)
        }    
    </div>
};

export default Users;
