import React, { useState } from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileInfo.module.css';
import Preloader from '../../Preloader/Preloader';
import MyData from './MyData';
import ProfileStatusWithHooks from './ProfileStatusWithHooks';
import MyDataForm from './MyDataForm';

const ProfileInfo = ({profile, status, updateStatus, isOwner, saveProfile}) => {
    let [editMode, setEditMode] = useState(false);

    if (!profile) {
        return <Preloader />
    }

    const onSubmit = (formData) => {
        saveProfile(formData);
        setEditMode()
    }
    
    return (
        <div>
            <div className={classes.top_img}>
                <img 
                    src="https://avatars.dzeninfra.ru/get-zen_doc/196027/pub_5cb2e7b9c07b6700b34e992c_5cb2ebb66749e800b419fc58/scale_2400" 
                    alt=''/>
            </div>
            <div className={classes.fio}>
                {profile.fullName}
            </div>
            <ProfileStatusWithHooks 
                status={status}
                updateStatus={updateStatus} />
                {editMode 
                    ? <MyDataForm 
                        initialValues={profile}
                        profile={profile} 
                        onSubmit={onSubmit} /> 
                    : <MyData 
                        profile={profile} 
                        isOwner={isOwner}
                        onEditMode={ () => {setEditMode(true)} } />
                }
        </div>
    );
}

export default ProfileInfo;
