import React from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileInfo.module.css';
import noPhoto from '../../../assets/img/no-photo.png';
import MyContact from './MyContacts';

const MyData = (props) => {
    const onMyProfilePhotoUploading = (e) => {
        if(e.target.files.length) {
            props.savePhoto(e.target.files[0])
        }
    }

    return (
        <div>
            {props.isOwner && <div>
                <button onClick={editMode}>Редактировать профиль</button>
            </div>}
            <div className={classes.info}>
                <div className={classes.avatar}>
                    <img 
                        src={props.profile.photos.large !== null ? props.profile.photos.large : noPhoto} 
                        alt='' 
                        className={classes.user_photo} />
                        {props.isOwner && <input type='file' onChange={onMyProfilePhotoUploading} />}
                </div>
                <div className={classes.data}>
                    <div>
                        {props.profile.aboutMe && 
                            <span><b>Обо мне:</b> {props.profile.aboutMe}</span>}
                        <span><b>Ищу работу:</b> {props.profile.lookingForAJob ? 'Да' : 'Нет'}</span>
                        {props.profile.lookingForAJobDescription && 
                            <span><b>Описание работы:</b> {props.profile.lookingForAJobDescription}</span>}
                        <span>
                            {Object.keys(props.profile.contacts).map(key => {
                                return <div className={classes.contacts_list}>
                                    <MyContact 
                                        key={key} 
                                        contactTitle={key} 
                                        contactValue={props.profile.contacts[key]} />
                                </div>
                            })}
                        </span>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MyData;
