import React from 'react';
import classes from '../../css/Dialogues/Dialogues.module.css';
import DialogueItem from '../Dialogues/DialogueItem/DialogueItem';
import Message from '../Dialogues/Message/Message';
import NewMessageContainer from '../Dialogues/Message/NewMessageContainer';

const Dialogues = (props) => {
    let state = props.pageDialogues;

    let dialoguesEls = state.dialoguesDt.map(chat => <DialogueItem name={chat.name} id={chat.id} image={chat.image} />);

    let messagesEls = state.messagesDt.map(msg => <Message message={msg.message} sendFrom={msg.sender} />);

    return (
        <div className={classes.dialogues}>
            <div className={classes.dialogues_items}>
                {dialoguesEls}
            </div>
            <div className={classes.messages}>
                {messagesEls}
                <div>
            <form className={classes.form}>
                <textarea onChange={onMsgChange} value={props.newMessageText} />
                <button type='button' onClick={addMsg}>
                    Отправить
                </button>
            </form>
        </div>
            </div>
        </div>
    );
}

export default Dialogues;
