import React from 'react';
import Dialogues from './Dialogues';
import { connect } from 'react-redux';
import {addMsgCreator, newMsgPostCreator} from '../../redux/dialoguesReducer';

/*const DialoguesContainer = (props) => {
    let state = props.store.getState().pageDialogues;

    let addMsg = () => {
        props.store.dispatch(addMsgCreator());
    };

    let onMsgChange = (msgText) => {
        props.store.dispatch(newMsgPostCreator(msgText));
    };

    return (
        <Dialogues 
            pageDialogues={state} 
            newMsgPost={onMsgChange} 
            addMsg={addMsg} 
            newMessageText={state.newMessageText} />
    );
}*/

let mapStateToProps = (state) => {
    return {
        pageDialogues: state.pageDialogues
    }
};

let mapDispatchToProps = (dispatch) => {
    return {
        newMsgPost: (msgText) => {
            dispatch(newMsgPostCreator(msgText));
        },
        addMsg: () => {
            dispatch(addMsgCreator());
        }
    }
};

const DialoguesContainer = connect(mapStateToProps, mapDispatchToProps)(Dialogues);

export default DialoguesContainer;
