const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';

let initialState = {
    users: [
        /*{
            id: 1,
            photoURL: 'https://shapka-youtube.ru/wp-content/uploads/2021/02/avatarka-dlya-skaypa-dlya-parney.jpg',
            fullName: 'Святослав',
            status: 'Big Boss',
            followed: false,
            location: {
                city: 'Новосибирск',
                country: 'Россия'
            }
        },
        {
            id: 2,
            photoURL: 'https://cs14.pikabu.ru/post_img/big/2023/02/13/8/1676295806139337963.png',
            fullName: 'Марина',
            status: 'Big Boss',
            followed: true,
            location: {
                city: 'Минск',
                country: 'Беларусь'
            }
        },
        {
            id: 3,
            photoURL: 'https://masterpiecer-images.s3.yandex.net/fec2be5d968d11eeb6343a7ca4cc1bdc:upscaled',
            fullName: 'Aaron',
            status: 'Big Boss',
            followed: true,
            location: {
                city: 'NY',
                country: 'USA'
            }
        }*/
    ]
};

const usersReducer = (state = initialState, action) => {
    switch(action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SET_USERS:
            return {
                ...state,
                users: [...state.users, ...action.users]
                //users: [...action.users]
            }
        default:
            return state;
    };
};

export const followAC = (userId) => ({type: FOLLOW, userId});

export const unfollowAC = (userId) => ({type: UNFOLLOW, userId});

export const setUsersAC = (users) => ({type: SET_USERS, users});

export default usersReducer;
