import React from 'react';
import classes from '../../css/Users/Users.module.css';

const Users = (props) => {

    props.setUsers({});

    return <div>
        {
            props.users.map(u => <div key={u.id}>
                <div>
                    <img src={u.photoURL} alt='' className={classes.user_photo} />
                    <div>
                        {u.followed 
                        ? <button type='button' onClick={ () => { props.unfollow(u.id) } }>Отписаться</button> 
                        : <button type='button' onClick={ () => { props.follow(u.id) } }>Подписаться</button>}
                    </div>
                </div>
                <div>
                    <div>
                        <span>{u.fullName}</span>
                        <span>{u.status}</span>
                    </div>
                    <div>
                        <span>{u.location.city}</span>
                        <span>{u.location.country}</span>
                    </div>
                </div>
            </div>)
        }    
    </div>
};

export default Users;
