import React from 'react';
import classes from '../../css/Auth/Auth.module.css';
import { reduxForm } from 'redux-form';

const Auth = (props) => {
    return <div>
        <h1 className={classes.page_title}>
            Авторизация
        </h1>
        <SignInReduxForm />
    </div>
}

const SignInForm = (props) => {
    return (
        <form className={classes.signin_form}>
            <div>
                <Field 
                    component={'input'}
                    placeholder={'Имя'} />
            </div>
            <div>
                <Field 
                    component={'input'}
                    placeholder={'Пароль'} />
            </div>
            <div>
                <Field type={'checkbox'} />
                Запомнить меня
            </div>
            <div>
                <button type={'submit'}>
                    Авторизоваться
                </button>
            </div>
        </form>
    )
}

const SignInReduxForm = reduxForm({form: 'signIn'})(SignInForm)

export default Auth;
