import React from 'react';
import {addMsgCreator, newMsgPostCreator} from '../../../redux/dialoguesReducer';
import NewMessage from '../Message/NewMessage';

const NewMessageContainer = (props) => {
    let state = props.pageDialogues.getState();
    let addMsg = () => {
        props.store.dispatch(addMsgCreator());
    };

    let onMsgChange = (msgText) => {
        props.store.dispatch(newMsgPostCreator(msgText));
    };

    return (
        <NewMessage 
            newMsgPost={onMsgChange} 
            addMsg={addMsg} 
            newMessageText={state.newMessageText} />
    );
}

export default NewMessageContainer;
