const ADD_POST = 'ADD-POST';
const REWRITE_POST_TEXT = 'REWRITE-POST-TEXT';
const SET_USER_PROFILE = 'SET_USER_PROFILE';

let initialState = {
    postsDt: [
        {
            id: 1,
            message: 'Hi, how are u?',
            likes: 12
        },
        {
            id: 2,
            message: 'Hi, everybody!',
            likes: 36
        },
        {
            id: 3,
            message: 'OK. And u?',
            likes: 26
        }
    ],
    newPostText: 'IT project',
    userProfile: null
};

const profileReducer = (state = initialState, action) => {
    switch(action.type) {
        case ADD_POST: {
            return {
                ...state,
                newPostText: '',
                postsDt: [...state.postsDt, {
                    id: state.postsDt.length + 1,
                    message: state.newPostText,
                    likes: 0
                }]
            };
        }
        case REWRITE_POST_TEXT: {
            return {
                ...state,
                newPostText: action.newTxt
            };
        }
        case SET_USER_PROFILE:
            return {
                ...state,
                userProfile: action.userProfile
            };
        default:
            return state;
    };
};

//dispatch
export const addPostActionCreator = () => ({type: ADD_POST});
export const rewritePostTextActionCreator = (text) => ({type: REWRITE_POST_TEXT, newTxt: text});
export const setUserProfile = (userProfile) => ({type: SET_USER_PROFILE, userProfile});
export const getUserProfile = (userProfile) => ({type: SET_USER_PROFILE, userProfile});

//thunk


export default profileReducer;
