import React from 'react';
import classes from '../../css/Users/Users.module.css';
import axios from 'axios';
import userPhoto from '../../assets/img/no-pic.png'

class Users extends React.Component {
    componentDidMount() {
        axios
        .get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
        .then(response => {
            this.props.setUsers(response.data.items);
        });
    }
    onPageChanged = (e) => {
        
    }

    render() { 
        let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);
        let pages = [];
        for (let i=1; i <= pagesCount; i++) {
            pages.push(i);
        }
        return <div>
            <div className={classes.nav_pages}>
                {pages.map(p => {
                    return (
                        <span 
                        className={this.props.currentPage === p && classes.selected_page ||  classes.page}
                        onClick={() => {this.props.setCurrentPage(p)}}>
                            {p}
                        </span>
                    );
                })}
            </div>
            {
                this.props.users.map(u => <div key={u.id}>
                    <div>
                        <img 
                            src={u.photos.small !== null ? u.photos.small : userPhoto} 
                            alt='' 
                            className={classes.user_photo} />
                        <div>
                            {u.followed 
                            ? <button onClick={ () => { this.props.unfollow(u.id) } }>Отписаться</button> 
                            : <button onClick={ () => { this.props.follow(u.id) } }>Подписаться</button>}
                        </div>
                    </div>
                    <div>
                        <div>
                            <span>{u.name}</span>
                            <span>{u.status}</span>
                        </div>
                        <div>
                            <span>{'u.location.city'}</span>
                            <span>{'u.location.country'}</span>
                        </div>
                    </div>
                </div>)
            }    
        </div>
    }
}

export default Users;
