import React from 'react';
import classes from '../../../css/Profile/ProfileInfo/ProfileInfo.module.css';
import Preloader from '../../Preloader/Preloader';
import noPhoto from '../../../assets/img/no-photo.png';
import ProfileStatusWithHooks from './ProfileStatusWithHooks'

const ProfileInfo = ({profile, status, upadateStatus, isOwner, savePhoto}) => {
    if (!profile) {
        return <Preloader />
    }
    
    return (
        <div>
            <div className={classes.top_img}>
                <img 
                    src="https://avatars.dzeninfra.ru/get-zen_doc/196027/pub_5cb2e7b9c07b6700b34e992c_5cb2ebb66749e800b419fc58/scale_2400" 
                    alt=''/>
            </div>
            <div className={classes.fio}>
                {profile.fullName}
            </div>
            <MyData profile={profile} />
        </div>
    );
}

const MyData = ({profile, status, isOwner}) => {
    return (
        <div className={classes.info}>
            <div className={classes.avatar}>
                <img 
                    src={profile.photos.large !== null ? profile.photos.large : noPhoto} 
                    alt='' 
                    className={classes.user_photo} />
                    {isOwner && <input type='file' onChange={onMyProfilePhotoUploading} />}
            </div>
            <div className={classes.data}>
                <ProfileStatusWithHooks status={status} updateStatus={updateStatus} />
                <div>
                    {profile.aboutMe && 
                        <span><b>Обо мне:</b> {profile.aboutMe}</span>}
                    <span><b>Ищу работу:</b> {profile.lookingForAJob ? 'Да' : 'Нет'}</span>
                    {profile.lookingForAJobDescription && 
                        <span><b>Описание работы:</b> {profile.lookingForAJobDescription}</span>}
                    <span>
                        {Object.keys(profile.contacts).map(key => {
                            return <div className={classes.contacts_list}>
                                <Contact key={key} contactTitle={key} contactValue={profile.contacts[key]} />
                            </div>
                        })}
                    </span>
                </div>
                    </div>
        </div>
    )
}

const Contact = ({contactTitle, contactValue}) => {
    return <span>
        <b>{contactTitle}:</b> {contactValue}
    </span>
}

export default ProfileInfo;
