import React from 'react';
import classes from '../../css/Users/Users.module.css';
import noPhoto from '../../assets/img/no-photo.png';
import { NavLink } from 'react-router-dom';
import Paginator from './Paginator';

const Users = ({currentPage, onPageChanged, totalUsersCount, pageSize, ...props}) => {
    let pagesCount = Math.ceil(totalUsersCount / pageSize);
    let pages = [];
    for (let i=1; i <= pagesCount; i++) {
        pages.push(i);
    }
    
    return <div>
        <Paginator 
            currentPage={currentPage} onPageChanged={onPageChanged} totalUsersCount={totalUsersCount} pageSize={pageSize} />
        <div className={ classes.users_list }>
            {
                props.users.map(u => 
                    <div key={ u.id }>
                        <div className={ classes.info }>
                            <NavLink to={ '/profile/' + u.id }>
                                <img 
                                    src={u.photos.small !== null ? u.photos.small : noPhoto} 
                                    alt='' 
                                    className={classes.user_photo} />
                            </NavLink>
                            <div className={classes.user_info}>
                                <div>
                                    <span className={classes.u_name}>{u.name}</span>
                                    <span>{u.status}</span>
                                </div>
                                <div>
                                    <span>{'u.location.city'}</span>
                                    <span>{'u.location.country'}</span>
                                </div>
                            </div>
                        </div>
                        <div className={classes.follow_buttons}>
                            {u.followed 
                            ? <button 
                                disabled={props.followingInProgress.some(id => id === u.id)} 
                                className={classes.unfollow_btn} 
                                onClick={ () => {props.unfollow(u.id)}}>
                                    Отписаться</button> 
                            : <button 
                                disabled={props.followingInProgress.some(id => id === u.id)}
                                className={classes.follow_btn} 
                                onClick={ () => {props.follow(u.id)}}>
                                Подписаться</button>
                            }
                        </div>
                    </div>
                )
            }
        </div> 
    </div>
}

export default Users;
