const ADD_MSG = 'ADD-MSG';
const NEW_MSG_POST = 'NEW-MSG-POST';

let initialState = {
    dialoguesDt: [
        {
            id: 1,
            name: 'Мария',
            image: 'avatar_1.png'
        },
        {
            id: 2,
            name: 'Антон',
            image: 'avatar_2.jpg'
        },
        {
            id: 3,
            name: 'Мартин',
            image: 'avatar_5.jpg'
        },
        {
            id: 4,
            name: 'Алексей',
            image: 'avatar_4.jpg'
        },
        {
            id: 5,
            name: 'Евгения',
            image: 'avatar_3.jpg'
        }
    ],
    messagesDt: [
        {
            id: 1,
            message: 'Hi!',
            sender: 'friend'
        },
        {
            id: 2,
            message: 'How r u?',
            sender: 'friend'
        },
        {
            id: 3,
            message: 'OK. And you?',
            sender: 'im'
        },
        {
            id: 4,
            message: 'Awesome!',
            sender: 'friend'
        }
    ],
    newMessageText: 'IT development'
};

const dialoguesReducer = (state = initialState, action) => {

    
    switch(action.type) {
        case ADD_MSG: {
            let newMsg = state.newMessageText;
            let stateCopy = {...state};
            stateCopy.messagesDt.push({
                id: 5,
                message: newMsg,
                sender: 'im'
            });
            stateCopy.newMessageText = '';
            return stateCopy;
        }
        case NEW_MSG_POST: {
            let stateCopy = {...state};
            stateCopy.newMessageText = action.newMsg;
            return stateCopy;
        }
        default:
            return state;
    };
};

export const addMsgCreator = () => ({type: ADD_MSG});

export const newMsgPostCreator = (msgText) => ({type: NEW_MSG_POST, newMsg: msgText});

export default dialoguesReducer;
