import React from 'react';
import Dialogues from './Dialogues';
import { connect } from 'react-redux';
import {addMsgCreator, newMsgPostCreator} from '../../redux/dialoguesReducer';
import { withAuthRedirect } from '../../hoc/AuthRedirect';

let mapStateToProps = (state) => {
    return {
        pageDialogues: state.pageDialogues,
        isAuthorized: state.auth.isAuthorized
    }
};

let mapDispatchToProps = (dispatch) => {
    return {
        newMsgPost: (msgText) => {
            dispatch(newMsgPostCreator(msgText));
        },
        addMsg: () => {
            dispatch(addMsgCreator());
        }
    }
};

let AuthRedirectComponent = withAuthRedirect(Dialogues);

const DialoguesContainer = connect(mapStateToProps, mapDispatchToProps)(AuthRedirectComponent);

export default DialoguesContainer;
