import React from 'react';
import classes from '../../css/Users/Users.module.css';
import userPhoto from '../../assets/img/no-pic.png';
import { NavLink } from 'react-router-dom';
import { unfollowUser, followUser } from '../../api/api';

const Users = (props) => {
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i=1; i <= pagesCount; i++) {
        pages.push(i);
    }
    
    return <div>
        <div className={classes.nav_pages}>
            {pages.map(p => {
                return (
                    <span 
                    key={p.id}
                    className={props.currentPage === p && classes.selected_page || classes.page}
                    onClick={(e) => {props.onPageChanged(p)}}>
                        {p}
                    </span>
                );
            })}
        </div>
        {
            props.users.map(u => <div key={u.id}>
                <div>
                    <NavLink to={'/profile/' + u.id}>
                        <img 
                            src={u.photos.small !== null ? u.photos.small : userPhoto} 
                            alt='' 
                            className={classes.user_photo} />
                    </NavLink>
                    <div>
                        {u.followed 
                        ? <button onClick={ () => { 
                            usersAPI.unfollowUser(u.id).then(data => {
                                if (data.resultCode == 0) {
                                    props.unfollow(u.id)
                                };
                            }); 
                        } }>Отписаться</button> 
                        : <button onClick={ () => {
                            usersAPI.followUser(u.id).then(data => {
                                if (data.resultCode == 0) {
                                    props.unfollow(u.id)
                                };
                            }); 
                            axios
                                .post(`https://social-network.samuraijs.com/api/1.0/follow/${u.id}`, {}, {
                                    withCredentials: true,
                                    headers: {
                                        'API-KEY': '42065ba3-5e5d-40d4-943f-51614d3c588d'
                                    }
                                })
                                .then(response => {
                                    if (response.data.resultCode == 0) {
                                        props.follow(u.id);
                                    };
                                }); 
                        } }>Подписаться</button>}
                    </div>
                </div>
                <div>
                    <div>
                        <span>{u.name}</span>
                        <span>{u.status}</span>
                    </div>
                    <div>
                        <span>{'u.location.city'}</span>
                        <span>{'u.location.country'}</span>
                    </div>
                </div>
            </div>)
        }    
    </div>
}

export default Users;
