import React from 'react';
import Post from '../MyPosts/Post';
import classes from '../../../css/Profile/MyPosts/MyPost.module.css';
import {addPostActionCreator, rewritePostTextActionCreator} from '../../../redux/profileReducer'

const MyPosts = (props) => {
    let postsEls = props.posts.map(msg => <Post message={msg.message} likes={msg.likes} />);

    let newPostEl = React.createRef();

    let addPost = () => {
        //props.addPost();
    };

    let onPostChange = () => {
        let text = newPostEl.current.value;
        props.updateNewPostText(text);
    };

    return (
        <div className={classes.entries}>
            <p>My posts</p>
            <div>
                <form>
                    <textarea onChange={onPostChange} ref={newPostEl} value={props.newPostText} />
                    <button type='button' onClick={addPost}>
                        Отправить
                    </button>
                </form>
            </div>
            {postsEls}
        </div>
    );
}

export default MyPosts;
