import React from 'react';
import classes from '../../../css/Dialogues/Message/NewMessage.module.css';
import {addMsgCreator, newMsgPostCreator} from '../../../redux/dialoguesReducer'

const NewMessage = (props) => {
    let addMsg = () => {
        props.dispatch(addMsgCreator());
    };

    let onMsgChange = (e) => {
        let msgText = e.target.value;
        action = newMsgPostCreator(msgText);;
        props.dispatch(action);
    };

    return (
        <div>
            <form className={classes.form}>
                <textarea onChange={onMsgChange} value={props.newMessageText} />
                <button type='button' onClick={addMsg}>
                    Отправить
                </button>
            </form>
        </div>
    );
}

export default NewMessage;
